import React, { useEffect, useRef } from 'react';
import "./wave.css"

const Wave = () => {
    const waveRef = useRef(null);

    useEffect(() => {
        if (waveRef.current) {
            const width = waveRef.current.clientWidth;
            waveRef.current.style.height = `${width}px`;
			
        }
    }, []);

    return (
        <div className="audiobox">
            <div className="wave one" ref={waveRef}></div>
            <div className="wave two" ref={waveRef}></div>
            <div className="wave three" ref={waveRef}></div>
        </div>
    );
};

export default Wave;
